import React from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import { Menu, Row, Col } from "antd";

const { SubMenu } = Menu;

const AppLayout = ({ children }) => {
  return (
    <>
      <div>
        <Menu mode="horizontal" triggerSubMenuAction="click" theme="dark">
          <SubMenu title="OneClickAI">
            <Menu.Item key="Option:1">Option 1</Menu.Item>
            <Menu.Item key="Option:2">Option 2</Menu.Item>
            <Menu.Item key="Option:3">Option 3</Menu.Item>
            <Menu.Item key="Option:4">Option 4</Menu.Item>
          </SubMenu>
          <Menu.Item>
            <Link href="/">
              <a>About</a>
            </Link>
          </Menu.Item>
          <Menu.Item>
            <Link href="/train">
              <a>Get Started</a>
            </Link>
          </Menu.Item>
        </Menu>
        {/* xs: 모바일, sm: 태블릿, md: 작은 데스크탑 */}
        {/* 최대크기는 24 */}
        {/* gutter 컬럼 사이의 간격 */}
        <Row gutter={8}>
          <Col xs={24} md={12}>
            설명1
          </Col>
          <Col xs={24} md={12}>
            그림
          </Col>
          <Col xs={24} md={24}>
            설명2
          </Col>
          <Col xs={24} md={24}>
            설명3
          </Col>
        </Row>
        {children}
      </div>
    </>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
