import React, { useRef, useCallback } from "react";
import Link from "next/link";
import { Button, Card, Col, Row } from "antd";
<Col xs={24} md={8}>
  <Card
    hoverable
    cover={
      <img alt="teach-images" src="images/teach-images.png" width="100%" />
    }
  >
    <Meta
      title="Image Project"
      description="Teach based on images, from files or your webcam"
    />
  </Card>
</Col>;

const { Meta } = Card;

const Train = () => {
  // 실제 DOM에 접근하기 위해
  const imageInput = useRef();

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);
  return (
    <>
      <div
        style={{
          padding: "0 calc(100% / 9 * 2)",
          marginTop: 84,
        }}
      >
        <Row gutter={30}>
          <Col xs={24} md={24} style={{ marginBottom: 30 }}>
            <h1>New Project</h1>
          </Col>
          <Col xs={24} md={24} style={{ marginBottom: 30 }}>
            <input type="file" multiple hidden ref={imageInput} />
            <Button onClick={onClickImageUpload}>File</Button>
          </Col>
          <Link href="/train/image"></Link>
          <Link href="/train/audio">
            <Col xs={24} md={8}>
              <Card
                hoverable
                cover={
                  <img
                    alt="teach-sounds"
                    src="images/teach-sounds.png"
                    width="100%"
                  />
                }
              >
                <Meta
                  title="Audio Project"
                  description="Teach based on one-second-logn sounds, from files or your microphone."
                />
              </Card>
            </Col>
          </Link>
          <Link href="/train/pose">
            <Col xs={24} md={8}>
              <Card
                hoverable
                cover={
                  <img
                    alt="teach-poses"
                    src="images/teach-poses.png"
                    width="100%"
                  />
                }
              >
                <Meta
                  title="Pose Project"
                  description="Teach based on images, from files or your webcam"
                />
              </Card>
            </Col>
          </Link>
        </Row>
      </div>
    </>
  );
};

export default Train;
