import React from "react";
import PropTypes from "prop-types";
import Link from "next/link";
import { Menu, Row, Col, Layout } from "antd";

const { SubMenu } = Menu;

const { Header, Content, Footer } = Layout;

const AppLayout = ({ children }) => {
  return (
    <>
      <Layout>
        <Header style={{ position: "fixed", zIndex: 1, width: "100%" }}>
          <Menu mode="horizontal" triggerSubMenuAction="click" theme="dark">
            <SubMenu title="OneClickAI">
              <Menu.Item key="Option:1">Option 1</Menu.Item>
              <Menu.Item key="Option:2">Option 2</Menu.Item>
              <Menu.Item key="Option:3">Option 3</Menu.Item>
              <Menu.Item key="Option:4">Option 4</Menu.Item>
            </SubMenu>
            <Menu.Item style={{ float: "right" }}>
              <Link href="/train">
                <a>Get Started</a>
              </Link>
            </Menu.Item>
            <Menu.Item style={{ float: "right" }}>
              <Link href="/">
                <a>About</a>
              </Link>
            </Menu.Item>
          </Menu>
        </Header>
        {/* xs: 모바일, sm: 태블릿, md: 작은 데스크탑 */}
        {/* 최대크기는 24 */}
        {/* gutter 컬럼 사이의 간격 */}
        <Content style={{ padding: "0 100px", marginTop: 64 }}>
          <Row gutter={8}>
            <Col xs={24} md={12}>
              설명1
            </Col>
            <Col xs={24} md={12}>
              그림
            </Col>
            <Col xs={24} md={24}>
              설명2
            </Col>
            <Col xs={24} md={24}>
              설명3
            </Col>
          </Row>
          {children}
        </Content>
        <Footer style={{ textAlign: "center" }}>
          ©2020 Created by OneClickAI
        </Footer>
      </Layout>
    </>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
