import React, { useRef, useCallback } from "react";
import Link from "next/link";
import { Button, Card, Col, Row } from "antd";

const { Meta } = Card;

const Train = () => {
  // 실제 DOM에 접근하기 위해
  const imageInput = useRef();

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
  }, [imageInput.current]);
  return (
    <>
      <div
        style={{
          padding: "0 calc(100% / 18 * 2)",
          marginTop: 84,
        }}
      >
        <Row gutter={8}>
          <div>
            <h1>New Project</h1>
          </div>
          <Col xs={24} md={24}>
            <input type="file" multiple hidden ref={imageInput} />
            <Button onClick={onClickImageUpload}>File</Button>
          </Col>
          <Col xs={24} md={8}>
            <Card
              hoverable
              style={{ height: 300 }}
              cover={
                <img
                  alt="teach-images"
                  src="images/teach-images.png"
                  width="100%"
                />
              }
            >
              <Meta
                title="Image Project"
                description="Teach based on images, from files or your webcam"
              />
            </Card>
          </Col>
          <Col xs={24} md={8}>
            <Card
              hoverable
              style={{ height: 300 }}
              cover={
                <img
                  alt="teach-sounds"
                  src="images/teach-sounds.png"
                  width="100%"
                />
              }
            >
              <Meta
                title="Audio Project"
                description="Teach based on one-second-logn sounds, from files or your microphone."
              />
            </Card>
          </Col>
          <Col xs={24} md={8}>
            <Card
              hoverable
              style={{ height: 300 }}
              cover={
                <img
                  alt="teach-poses"
                  src="images/teach-poses.png"
                  width="100%"
                />
              }
            >
              <Meta
                title="Pose Project"
                description="Teach based on images, from files or your webcam"
              />
            </Card>
          </Col>
          <Link href="/train/audio">오디오</Link>
          <Link href="/train/image">이미지</Link>
          <Link href="/train/pose">포즈</Link>
        </Row>
      </div>
    </>
  );
};

export default Train;
